package org.javalearning.methods;

public class OperatorOverloadingExample {

	// In Java OperatorOverloading concept exists only for "+" operator
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(10+20);
		System.out.println("Pallavi" + " " + "Anand");
		System.out.println(10+20+ " Pallavi" + " Anand");
		System.out.println(10+20+ " Pallavi" + " Anand" + 100 + 200);

	}

}
